import React, { useState, useEffect } from 'react';
import axios from 'axios';

const KAKAO_API_KEY = 'f2866ffa27f4403047eecb4cc9721bcd'; // 실제 API 키로 변경하세요
const DIRECTIONS_URL = 'https://apis-navi.kakaomobility.com/v1/waypoints/directions';

const fetchDirections = async () => {
  try {
    const response = await axios.post(
      DIRECTIONS_URL,
      {
        origin: { x: "127.11024293202674", y: "37.394348634049784" },
        destination: { x: "127.10860518470294", y: "37.401999820065534" },
        waypoints: [
          {
            name: "name0",
            x: 127.11341936045922,
            y: 37.39639094915999
          }
        ],
        priority: "RECOMMEND",
        car_fuel: "GASOLINE",
        car_hipass: false,
        alternatives: false,
        road_details: false
      },
      {
        headers: {
          Authorization: `KakaoAK ${KAKAO_API_KEY}`,
          'Content-Type': 'application/json',
        },
      }
    );
    console.log('API response:', response.data);
    return response.data;
  } catch (error) {
    console.error('Error fetching directions:', error.response || error.message);
    return null;
  }
};

const DirectionComponent = () => {
  const [route, setRoute] = useState(null);

  useEffect(() => {
    const getRoute = async () => {
      const directions = await fetchDirections();
      setRoute(directions);
    };

    getRoute();
  }, []);

  return (
    <div>
      <h1>Directions API 결과</h1>
      {route ? <pre>{JSON.stringify(route, null, 2)}</pre> : <p>경로 데이터를 불러오는 중입니다...</p>}
    </div>
  );
};

export default DirectionComponent;
