import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Map, Polyline, MapMarker } from 'react-kakao-maps-sdk';

const KAKAO_API_KEY = '892b3fa8ad217de4b2ca0cae7c1ca67b'; // 환경 변수로 API 키 관리
const DIRECTIONS_URL = 'https://apis-navi.kakaomobility.com/v1/waypoints/directions';

// API 호출 함수
const fetchDirections = async (origin, destination, waypoints) => {
  try {
    const response = await axios.post(
      DIRECTIONS_URL,
      {
        origin: { x: parseFloat(origin.lng), y: parseFloat(origin.lat) },
        destination: { x: parseFloat(destination.lng), y: parseFloat(destination.lat) },
        waypoints: waypoints.map((point, index) => ({
          name: `waypoint${index}`,
          x: parseFloat(point.lng),
          y: parseFloat(point.lat),
        })),
        priority: 'RECOMMEND',
        car_fuel: 'GASOLINE',
        car_hipass: false,
        alternatives: false,
        road_details: false,
      },
      {
        headers: {
          Authorization: `KakaoAK ${KAKAO_API_KEY}`,
          'Content-Type': 'application/json',
        },
      }
    );
    console.log('API response:', response.data);
    return response.data;
  } catch (error) {
    console.error('Error fetching directions:', error.response ? error.response.data : error.message);
    return null;
  }
};

// 폴리라인의 경로 추출 함수
const extractPathFromVertexes = (vertexes) => {
  const path = [];
  for (let i = 0; i < vertexes.length; i += 2) {
    if (vertexes[i + 1] !== undefined) {
      path.push({ lat: vertexes[i + 1], lng: vertexes[i] });
    }
  }
  return path;
};

// 메인 컴포넌트
const Tripsub = ({
  origin = { lat: 37.5665, lng: 126.9780 }, // 기본값: 서울시청 좌표
  destination = { lat: 37.4602, lng: 126.4407 }, // 기본값: 인천공항 좌표
  waypoints = [] // 경유지 배열
}) => {
  const [route, setRoute] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getRoute = async () => {
      if (!origin || !destination) {
        setError("출발지와 도착지를 설정하세요.");
        return;
      }
    
      const directions = await fetchDirections(origin, destination, waypoints);
      if (directions && directions.routes && directions.routes[0]) {
        setRoute(directions.routes[0]); // 첫 번째 경로 사용
      } else {
        setError('경로를 가져오는데 실패했습니다.');
      }
    };

    getRoute();
  }, [origin, destination, waypoints]);

  if (error) {
    return <div className='error-message'>{error}</div>;
  }

  if (!origin || !destination) {
    return <p>지도 정보를 불러오는 중입니다...</p>;
  }

  return (
    <div className="kakaomap">
      <div className="container">
        <div className="title">경로 안내 지도</div>
        <div className="map-container">
          <Map
            center={{ lat: origin.lat, lng: origin.lng }}
            style={{ width: '100%', height: '400px' }}
            level={3}
            appkey={KAKAO_API_KEY}
          >
            {/* 출발지 마커 */}
            <MapMarker position={{ lat: origin.lat, lng: origin.lng }} />

            {/* 도착지 마커 */}
            <MapMarker position={{ lat: destination.lat, lng: destination.lng }} />

            {/* 경유지 마커 */}
            {waypoints.length > 0 &&
              waypoints.map((point, index) => (
                <MapMarker key={index} position={{ lat: point.lat, lng: point.lng }} />
              ))}

            {/* 경로 폴리라인 */}
            {route && route.sections && (
              <Polyline
                path={route.sections.flatMap(section =>
                  section.roads ? section.roads.flatMap(road => extractPathFromVertexes(road.vertexes)) : []
                )}
                strokeWeight={5}
                strokeColor="#FF0000"
                strokeOpacity={0.7}
                strokeStyle="solid"
              />
            )}
          </Map>
        </div>

        <div className="location-info">
          <h3>경로 정보</h3>
          {waypoints.map((point, index) => (
            <div key={index} className="location-item">
              <h4>{`경유지 ${index + 1}`}</h4>
              <p>여행하는 장소의 간략한 설명</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Tripsub;
