function ImageSlider() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const imageSlides = [
    {
      image: seoul,
      background: slide1,
      title: "자연과 전통이 어우러진\n여행지, 전라남도",
      description: "푸른 바다와 드넓은 산이 빚어내는 아름다움, 전라남도에서 잊지 못할 여정을 시작하세요.",
    },
    {
      image: seoulmainimg,
      background: seoulmainimg,
      title: "롯데월드타워",
      description: "서울의 야경을 볼 수 있는 롯데월드타워",
    },
    {
      image: "https://media.istockphoto.com/id/1083573614/ko/%EC%82%AC%EC%A7%84/%ED%95%B4%EC%95%88-%EC%A0%88%EB%B2%BD-vd702%EC%9D%98-%EB%B0%98%EC%98%81.webp",
      background: "https://media.istockphoto.com/id/1083573614/ko/%EC%82%AC%EC%A7%84/%ED%95%B4%EC%95%88-%EC%A0%88%EB%B2%BD-vd702%EC%9D%98-%EB%B0%98%EC%98%81.webp",
      title: "해안절벽",
      description: "환상적인 일몰을 볼 수 있는 장소",
    },
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => 
        prevIndex === imageSlides.length - 1 ? 0 : prevIndex + 1
      );
    }, 3000);

    return () => clearInterval(interval);
  }, [imageSlides.length]);

  return (
    <div
      className="image-slider"
      style={{
        backgroundImage: `url(${imageSlides[currentImageIndex].background})`,
      }}
    >
      <div className="overlay">
        <h1>{imageSlides[currentImageIndex].title}</h1>
        <p>{imageSlides[currentImageIndex].description}</p>
      </div>
      <div className="slider-images">
        <div
          className="slider-container"
          style={{ transform: `translateX(-${currentImageIndex * 100}%)` }}
        >
          {imageSlides.map((slide, index) => (
            <div
              className={`slide ${currentImageIndex === index ? 'active' : ''}`}
              key={index}
            >
              <img src={slide.image} alt={`Slide ${index + 1}`} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
