import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UserNavbar from "../../components/UserNavbar/UserNavbar";
import './Setting/Setting.css';

const Setting = () => {
  const [userInfo, setUserInfo] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchUserInfo = async () => {
      const token = sessionStorage.getItem('accessToken'); // 로그인 시 저장한 accessToken
      const username = sessionStorage.getItem('username'); // 로그인 시 저장한 username
      const apiUrl = 'https://port-0-yeoga-backend-m1hgzlk8a26c4004.sel4.cloudtype.app';

      if (!token || !username) {
        setError('로그인 정보가 없습니다. 다시 로그인 해주세요.');
        return;
      }

      try {
        // 서버에 저장된 사용자 상세 정보 요청
        const response = await axios.get(`${apiUrl}/my/${username}`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setUserInfo(response.data);  // 받아온 사용자 정보를 상태에 저장
      } catch (error) {
        console.error('사용자 정보를 가져오는 데 실패했습니다:', error);
        setError('사용자 정보를 가져오는 데 실패했습니다.');
      }
    };

    fetchUserInfo();
  }, []);

  if (error) {
    return <div>{error}</div>;
  }

  if (!userInfo) {
    return <div>로딩 중...</div>;
  }

  return (
    <div className="Setting-page">
      <UserNavbar />
      <div className="Setting-page-container">
        <div className="Setting-page-container-section">
          <div className="Setting-page-container-User-img"></div>
          <div className="Setting-page-container-User-imgchange">프로필 변경하기</div>
          <div className="Setting-page-User">사용자 정보</div>

          <form>
            <div className="Setting-page-form-div">
              <div className='Setting-page-form-title'>
                <div className='Setting-page-form-text'>
                  <p className="Setting-page-file">이름</p>
                </div>
              </div>
              <input type="text" value={userInfo.name || ''} readOnly />
            </div>

            <div className="Setting-page-form-div">
              <p className="Setting-page-file">이메일</p>
              <input type="text" value={userInfo.email || ''} readOnly />
            </div>
            
            <div className="Setting-page-form-div">
              <p className="Setting-page-file">전화번호</p>
              <input type="text" value={userInfo.tel || ''} readOnly />
            </div>

            <div className="Setting-page-form-div">
              <p className="Setting-page-file">생년월일</p>
              <input type="text" value={userInfo.birthday || ''} readOnly />
            </div>

            <div className="Setting-page-form-div">
              <p className="Setting-page-file">아이디</p>
              <input type="text" value={userInfo.username || ''} readOnly />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Setting;
