import React, { useState } from 'react';
import axios from 'axios';

const Test2 = () => {
  const [touristData, setTouristData] = useState([]);
  const [error, setError] = useState(null);

  const serviceKey = encodeURIComponent('7qIlmVuNGV7pzIFYHRANmtd94tO1QGcC7a3hJRjj7lEshFvVHSRyw9AR9IhSHMoHYz2VV7uif6Wf5u517bNjlg%3D%3D');
  const url = 'https://apis.data.go.kr/B551011/TarRlteTarService/searchKeyword';

  const fetchTouristData = async () => {
    try {
      const response = await axios.get(url, {
        params: {
          serviceKey: serviceKey,
          numOfRows: '10',
          pageNo: '1',
          MobileOS: 'ETC',
          MobileApp: 'AppTest',
          _type: 'json',
          baseYm: '202404',
          areaCd: '11',
          sigunguCd: '11110',
          keyword: '경복궁'
        }
      });

      // API 응답 데이터 확인 및 구조화
      console.log("API 응답 전체:", response.data); // 전체 응답 구조 확인
      if (response.data && response.data.response && response.data.response.header) {
        if (response.data.response.header.resultCode === "0000") {
          setTouristData(response.data.response.body.items.item || []);
          setError(null);
        } else {
          setError(`에러 코드 ${response.data.response.header.resultCode}: ${response.data.response.header.resultMsg}`);
        }
      } else {
        setError('서버로부터 예상하지 못한 응답을 받았습니다.');
      }
    } catch (err) {
      console.error("Error:", err);
      console.error("Error response data:", err.response?.data);
      setError('관광지 정보를 가져오는 데 실패했습니다.');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchTouristData();
  };

  return (
    <div className="tourist-container">
      <h2>관광지 정보</h2>
      <form onSubmit={handleSubmit}>
        <button type="submit">Get Tourist Info</button>
      </form>

      {error && <p className="error-message" style={{ color: 'red' }}>{error}</p>}

      {touristData.length > 0 && (
        <div className="tourist-info">
          <ul>
            {touristData.map((item, index) => (
              <li key={index}>
                <h3>{item.tAtsNm}</h3>
                <p>기본 주소: {item.rlteBsicAdres}</p>
                <p>관광지 명: {item.rlteTatsNm}</p>
                <p>지역명: {item.areaNm}</p>
                <p>카테고리: {item.rlteCtgryLclsNm} - {item.rlteCtgryMclsNm}</p>
                <p>연관 순위: {item.rlteRank}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Test2;
