const TripCourse = () => {


    {/* 수정중 */}
    const [cardData, setCardData] = useState([]);
    const [tripnews, setTripnews] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchTouristSpots = async () => {
            try {
                const serviceKey = process.env.REACT_APP_API_KEY_openapi;
                const response = await axios.get("http://apis.data.go.kr/B551011/KorService1/searchFestival1", {
                    params: {
                        serviceKey: serviceKey,
                        numOfRows: 1,
                        MobileOS: 'ETC',
                        MobileApp: 'AppTest',
                        _type: 'json',
                        arrange: 'R',
                        listYN: 'Y',
                        eventStartDate: '20241001'
                    }
                });

                if (response.data.response.body.items) {
                    setTripnews(response.data.response.body.items.item);
                    
                } else {
                    setError("데이터를 불러오지 못했습니다.");
                }
            } catch (err) {
                setError("API 요청 중 오류가 발생했습니다.");
                console.error(err);
            }
        };

        fetchTouristSpots();
    }, []);



 // Google Places API로 숙소 데이터 가져오기
 useEffect(() => {
    const fetchLodgingData = async () => {
        try {
            const response = await axios.get('https://cors-anywhere.herokuapp.com/https://maps.googleapis.com/maps/api/place/nearbysearch/json', {
                params: {
                    location: '37.7749,-122.4194', // 예시: 원하는 위치의 위도와 경도
                    radius: 5000, // 검색 범위 (미터 단위)
                    keyword: '숙소', // 검색 키워드 
                    key: process.env.REACT_APP_GOOGLE_API_KEY // 발급받은 Google API 키
                }
            });

            // 필요한 데이터만 추출하여 cardData 형식으로 변환
            const fetchedData = response.data.results.map((place) => ({
                image: place.photos && place.photos.length > 0 
                    ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${place.photos[0].photo_reference}&key=${process.env.REACT_APP_GOOGLE_API_KEY}`
                    : 'https://via.placeholder.com/150', // 사진이 없는 경우 기본 이미지
                title: place.name,
                description: place.vicinity,
                rating: place.rating || '평점 없음'
            }));

            setCardData(fetchedData);
        } catch (error) {
            console.error("Error fetching lodging data:", error);
        }
    };

    fetchLodgingData();
}, []);










    const [currentIndex, setCurrentIndex] = useState(0);
    const [tripcurrentIndex, setTripcurrentIndex] = useState(0);
    const currentBackgroundColor = hotelimages[tripcurrentIndex].backgroundColor;


    //수정 필요!
    
    const prevSlide = () => {
        const newIndex = tripcurrentIndex === 0 ? hotelimages.length - 1 : tripcurrentIndex - 1;
        setTripcurrentIndex(newIndex);
    };

    const nextSlide = () => {
        const newIndex = tripcurrentIndex === hotelimages.length - 1 ? 0 : tripcurrentIndex + 1;
        setTripcurrentIndex(newIndex);
    };

    useEffect(() => {
        const interval = setInterval(() => {
            nextSlide();
        }, 3000);

        return () => clearInterval(interval);
    }, [tripcurrentIndex]);
   


    const prevCard = () => {
        if (currentIndex > 0) {
            setCurrentIndex(currentIndex - 1);
        }
    };
    
    const nextCard = () => {
        if (currentIndex < cardData.length - 4) { // 화면에 3개 카드가 보여지도록 설정
            setCurrentIndex(currentIndex + 1);
        }
    };
    

    const Card = ({ image, title, description, rating }) => {
        const [isLiked, setIsLiked] = useState(false);
        const renderStars = (rating) => {
            const fullStars = Math.floor(rating); // 가득 찬 별의 개수
            const stars = [];
            
    
            // 가득 찬 별 추가
            for (let i = 0; i < fullStars; i++) {
                stars.push(<span key={i} className="star full">★</span>);
            }
    
            // 빈 별 추가
            for (let i = fullStars; i < 5; i++) {
                stars.push(<span key={i} className="star empty">☆</span>);
            }
    
            return stars;
        };

        const toggleHeart = () => {
            setIsLiked(!isLiked);
        };

        return (
            <div className="card">
                <div className="image-container">
                    <img src={image} alt={title} className="card-image" />
                </div>
                <div className="card-content">
                    <div className="card-title">{title}
                    <span 
                        className={`heart ${isLiked ? 'liked' : ''}`} 
                        onClick={toggleHeart}> ♡
                    </span>
                    </div>
                
                    <div className="card-description">주소 : {description}</div>
                    <div className="card-rating">평점 : {renderStars(rating)}</div>
                   
                </div>
            </div>
        );
    };

    const Tripnews = ({ title, description }) => {
        return (
            <div className='trip-item'>
                <div className='trip-title'>{title}</div>
                <div className='trip-description'>{description}</div>
            </div>
        );
    };

    return (
        <div className="trip-contents" style={{  transition: 'background-color 0.5s ease' }}>
      <div className="slider">
            <img 
                src={hotelimages[tripcurrentIndex].hotelimage} 
                alt={hotelimages[tripcurrentIndex].hoteltitle || "slide"} 
                className='imageslider'/>
            
            <div className='tripCourse-slider-background'style={{ backgroundColor: currentBackgroundColor,transform: `translateX(-${tripcurrentIndex * 0}%)`, transition: 'transform 0.5s ease-in-out' }}>
                <h2 className='tripCourse-slider-title'>{hotelimages[tripcurrentIndex].hoteltitle || "여행 코스추천"}</h2>
                <div className='tripCourse-slider-contents-list'>
                <p className='sliderread'>{hotelimages[tripcurrentIndex].courselist || ""}</p>
                 <p className='sliderread'>{hotelimages[tripcurrentIndex].courselist2 || ""}</p>
                </div>
            </div>
        </div>

        <div className='lodging'>
                <h2>최근 많이 방문된 숙소예요</h2>
                <div className="lodgings">
                    {cardData.map((card, index) => (
                        <Card key={index} image={card.image} title={card.title} description={card.description} />
                    ))}
                </div>
            </div>
                        
            

         

            <div className='tripnews'>
                <div className='tripcontainer'>
                <div className='tripimg'>
            {tripnews.length > 0 && (
                <img 
                    src={tripnews[0].firstimage || "https://via.placeholder.com/150"} 
                    alt={tripnews[0].title} 
                    className='tripnews-image' 
                />
            )}
        </div>

        
                    <div className='triplist'>
                        <h2 className='trip-title-name'>이번주 여행소식</h2>
                        {error && <p>{error}</p>}   
                        {tripnews.map((trip, index) => (
                            <div key={index} className='trip-item'>
                                <div className='trip-text'>
                                    <div className='trip-title'>{trip.title}</div>
                                    <div className='trip-description'>시작일 : {trip.eventstartdate} ~ 종료일: {trip.eventenddate}</div>
                                    {/* <div>지역 코드: {trip.areacode}</div> */}
                                    <div>상세 주소: {trip.addr1}</div>
                                    {/* <div>전화번호: {trip.tel}</div> */}
                                </div>
                            </div>
                        ))}
            </div>  
        </div>
        </div>
        </div>
    );
};


export default TripCourse;
