import React, { useEffect, useState } from "react";
import axios from 'axios';

const Test2 = () => {
  const [data, setData] = useState([]);

  const serviceKey = '7qIlmVuNGV7pzIFYHRANmtd94tO1QGcC7a3hJRjj7lEshFvVHSRyw9AR9IhSHMoHYz2VV7uif6Wf5u517bNjlg%3D%3D';  // 발급받은 인증키 (URL 인코딩된 상태)
  const url = 'https://apis.data.go.kr/B551011/TarRlteTarService';  // API 엔드포인트

  // 파라미터 설정
  const params = {
    serviceKey: serviceKey,     // 인증키
    numOfRows: 10,              // 한 번에 가져올 데이터 수
    pageNo: 1,                  // 페이지 번호
    MobileOS: 'ETC',            // 운영체제 (기본: ETC)
    MobileApp: 'AppTest',       // 앱 이름 (테스트용)
    _type: 'json',              // 응답 형식 JSON으로 설정
    baseYm: '202404',           // 조회 기준 연월 (YYYYMM 형식)
    areaCd: '11',               // 지역 코드 (옵션)
    sigunguCd: '11530',         // 시군구 코드 (옵션)
  };

  useEffect(() => {
    axios.get(url, { params })
      .then(response => {
        console.log(response.data);  // 받아온 데이터를 확인
        setData(response.data.response.body.items.item);  // 데이터를 상태에 저장
      })
      .catch(error => {
        console.error('Error:', error);  // 에러 발생 시 출력
      });
  }, []);  // 컴포넌트가 처음 렌더링될 때 한 번만 실행

  return (
    <div className="aa">
      <h2>관광지 정보</h2>
      <ul>
        {data.map((item, index) => (
          <li key={index}>
            <h3>{item.title}</h3>  {/* 관광지 이름 */}
            <p>{item.addr1}</p>   {/* 관광지 주소 */}
            <img src={item.firstimage} alt={item.title} width="200" />  {/* 관광지 이미지 */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Test2;
