import React, { useState } from 'react';
import axios from 'axios';

const Test2 = () => {
  const [city, setCity] = useState('서울');  // 기본 도시 설정
  const [touristData, setTouristData] = useState(null);  // 관광지 데이터를 저장할 state
  const [error, setError] = useState(null);

  const serviceKey = '7qIlmVuNGV7pzIFYHRANmtd94tO1QGcC7a3hJRjj7lEshFvVHSRyw9AR9IhSHMoHYz2VV7uif6Wf5u517bNjlg%3D%3D';  // 발급받은 서비스키
  const url = `https://apis.data.go.kr/B551011/TarRlteTarService`;  // 관광지 API 엔드포인트

  const fetchTouristData = async () => {
    try {
      const params = {
        serviceKey: serviceKey,
        numOfRows: 10,        // 한 페이지당 결과 수
        pageNo: 1,            // 페이지 번호
        MobileOS: 'ETC',      // OS 구분
        MobileApp: 'TestApp', // 서비스명
        _type: 'json',        // 응답 형식
        baseYm: '202404',     // 기준 연월
        areaCd: 1,            // 지역 코드 (서울)
        sigunguCd: '11530',   // 시군구 코드
        keyword: '뮤지엄산'    // 검색 키워드 (관광지 이름)
      };

      const response = await axios.get(url, { params });
      setTouristData(response.data.response.body.items.item);  // 응답 데이터에서 필요한 부분 저장
      setError(null);  // 에러 초기화
    } catch (err) {
      console.error(err);  // 에러 출력
      setError('관광지 정보를 가져오는 데 실패했습니다.');
      setTouristData(null);  // 에러 발생 시 데이터 초기화
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchTouristData();  // 관광지 데이터를 가져오는 요청 실행
  };

  return (
    <div className="tourist-container">
      <h2>관광지 정보</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter city name"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button type="submit">Get Tourist Info</button>
      </form>

      {error && <p className="error-message">{error}</p>}

      {touristData && (
        <div className="tourist-info">
          <ul>
            {touristData.map((item, index) => (
              <li key={index}>
                <h3>{item.title}</h3>  {/* 관광지 이름 */}
                <p>{item.addr1}</p>   {/* 관광지 주소 */}
                <img src={item.firstimage} alt={item.title} width="200" />  {/* 관광지 이미지 */}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Test2;
