import React, { useEffect, useRef, useState } from 'react';

const KakaoMapSearch = () => {
    const [map, setMap] = useState(null);
    const [marker, setMarker] = useState(null);
    const mapRef = useRef(null);

    useEffect(() => {
        const kakao = window.kakao;

        // 지도를 생성합니다
        const mapContainer = mapRef.current;
        const mapOption = {
            center: new kakao.maps.LatLng(37.5665, 126.9780), // 초기 좌표 (서울 중심 예시)
            level: 3,
        };

        const mapInstance = new kakao.maps.Map(mapContainer, mapOption);
        setMap(mapInstance);

        // 초기 마커를 생성합니다
        const markerInstance = new kakao.maps.Marker({
            position: mapOption.center,
            map: mapInstance,
        });
        setMarker(markerInstance);
    }, []);

    // 장소 검색 함수
    const searchPlace = (placeName) => {
        const kakao = window.kakao;
        const places = new kakao.maps.services.Places();

        // 장소 검색 수행
        places.keywordSearch(placeName, (result, status) => {
            if (status === kakao.maps.services.Status.OK) {
                const firstResult = result[0];
                const coords = new kakao.maps.LatLng(firstResult.y, firstResult.x);

                // 지도의 중심을 검색 결과로 이동
                map.setCenter(coords);

                // 마커 위치 이동
                marker.setPosition(coords);
            } else {
                alert('검색 결과가 없습니다.');
            }
        });
    };

    return (
        <div>
            <div
                ref={mapRef}
                style={{ width: '100%', height: '400px' }}
            ></div>
            <input
                type="text"
                placeholder="장소를 입력하세요"
                onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                        searchPlace(e.target.value);
                    }
                }}
            />
        </div>
    );
};

export default KakaoMapSearch;
