import React, { useState } from 'react';
import axios from 'axios';

const Test2 = () => {
  const [city, setCity] = useState('서울');
  const [touristData, setTouristData] = useState(null);
  const [error, setError] = useState(null);

  const serviceKey ='7qIlmVuNGV7pzIFYHRANmtd94tO1QGcC7a3hJRjj7lEshFvVHSRyw9AR9IhSHMoHYz2VV7uif6Wf5u517bNjlg%3D%3D';
  const url = 'http://apis.data.go.kr/B551011/TarRlteTarService/searchKeyword';

  const fetchTouristData = async () => {
    try {
      const params = {
        serviceKey: serviceKey,
        numOfRows: 10,
        pageNo: 1,
        MobileOS: 'ETC',
        MobileApp: 'TestApp',
        _type: 'json',
        baseYm: '202404',
        areaCd: 51,
        sigunguCd: '11530',
        keyword: '뮤지엄산'
      };

      const response = await axios.get(url, {
        params: params,
        headers: {
          'Content-Type': 'application/json',
        }
      });

      // 응답이 예상한 구조인지 확인
      if (response.data && response.data.response && response.data.response.header) {
        if (response.data.response.header.resultCode === "0000") {
          if (response.data.response.body && response.data.response.body.items) {
            setTouristData(response.data.response.body.items.item);
            setError(null);
          } else {
            setError('데이터를 찾을 수 없습니다.');
          }
        } else {
          setError(`에러 코드 ${response.data.response.header.resultCode}: ${response.data.response.header.resultMsg}`);
        }
      } else {
        // 예상한 응답 구조가 아닐 경우
        console.error("Unexpected response structure:", response.data);
        setError('서버로부터 예상하지 못한 응답을 받았습니다.');
      }
    } catch (err) {
      console.error(err);
      console.error("Error response data:", err.response?.data);  // 에러 세부 데이터 출력
      setError('관광지 정보를 가져오는 데 실패했습니다.');
      setTouristData(null);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchTouristData();
  };

  return (
    <div className="tourist-container">
      <h2>관광지 정보</h2>
      <form onSubmit={handleSubmit}>
        <button type="submit">Get Tourist Info</button>
      </form>

      {error && <p className="error-message">{error}</p>}

      {touristData && (
        <div className="tourist-info">
          <ul>
            {touristData.map((item, index) => (
              <li key={index}>
                <h3>{item.tAtsNm}</h3>
                <p>기본 주소: {item.rltcBscAdres}</p>
                <p>관광지 명: {item.rltcTatsNm}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Test2;
