const handleSubmit = (e) => {
    e.preventDefault();
    const newFaqEntry = {
        no: faqData.length + 1,
        category: newFaq.category,
        title: newFaq.title,
        date: new Date().toISOString().split('T')[0] // 현재 날짜 설정
    };
    setFaqData([...faqData, newFaqEntry]); // 기존 데이터 뒤에 추가
    setNewFaq({ category: '', title: '', date: '' });
    setIsWriting(false);
};
