import React, { useEffect, useState } from "react";
import axios from 'axios';

const Test2 = () => {
  const [data, setData] = useState([]);  // 데이터를 저장할 state
  const serviceKey = '7qIlmVuNGV7pzIFYHRANmtd94tO1QGcC7a3hJRjj7lEshFvVHSRyw9AR9IhSHMoHYz2VV7uif6Wf5u517bNjlg%3D%3D';  // 발급받은 API 키
  const url = 'http://apis.data.go.kr/B551011/TarRlteTarService';  // API 엔드포인트

  // 파라미터 설정
  const params = {
    serviceKey: serviceKey,
    numOfRows: 10,            // 한 번에 불러올 데이터 수
    pageNo: 1,                // 페이지 번호
    MobileOS: 'ETC',          // 호출하는 OS 종류
    MobileApp: 'TestApp',      // 호출하는 앱 이름
    _type: 'json',            // 응답 형식을 JSON으로 지정
  };

  // useEffect를 사용하여 컴포넌트가 처음 렌더링될 때만 API 호출
  useEffect(() => {
    axios.get(url, { params })
      .then(response => {
        console.log(response.data);  // 받아온 데이터 출력 (개발 중 확인용)
        setData(response.data.response.body.items.item);  // 데이터를 state에 저장
      })
      .catch(error => {
        console.error('Error:', error);  // 에러 발생 시 출력
      });
  }, []);  // 빈 배열은 의존성이 없음을 의미, 즉 처음 렌더링 시 한 번만 실행

  return (
    <div className="aa">
      <h2>관광지 정보</h2>
      <ul>
        {data.map((item, index) => (
          <li key={index}>
            <h3>{item.title}</h3>  {/* 관광지 이름 */}
            <p>{item.addr1}</p>   {/* 관광지 주소 */}
            <img src={item.firstimage} alt={item.title} width="200" />  {/* 관광지 이미지 */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Test2;
