import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Map, Polyline, MapMarker } from 'react-kakao-maps-sdk';
import '../Tripsub/Tripsub.css';

const KAKAO_API_KEY = '892b3fa8ad217de4b2ca0cae7c1ca67b';
const DIRECTIONS_URL = 'https://apis-navi.kakaomobility.com/v1/waypoints/directions';

const fetchDirections = async (origin, destination, waypoints) => {
  try {
    const response = await axios.post(
      DIRECTIONS_URL,
      {
        origin: { x: parseFloat(origin.lng), y: parseFloat(origin.lat) },
        destination: { x: parseFloat(destination.lng), y: parseFloat(destination.lat) },
        waypoints: waypoints.map((point, index) => ({
          name: `waypoint${index}`,
          x: parseFloat(point.lng),
          y: parseFloat(point.lat),
        })),
        priority: 'RECOMMEND',
        car_fuel: 'GASOLINE',
        car_hipass: false,
        alternatives: false,
        road_details: false,
      },
      {
        headers: {
          Authorization: `KakaoAK ${KAKAO_API_KEY}`,
          'Content-Type': 'application/json',
        },
      }
    );
    return response.data;
  } catch (error) {
    console.error('Error fetching directions:', error.response ? error.response.data : error.message);
    return null;
  }
};

const extractPathFromVertexes = (vertexes) => {
  const path = [];
  for (let i = 0; i < vertexes.length; i += 2) {
    if (vertexes[i + 1] !== undefined) {
      path.push({ lat: vertexes[i + 1], lng: vertexes[i] });
    }
  }
  return path;
};

const generateRandomCoordinates = () => {
  const lat = 37.5 + Math.random() * 0.2;
  const lng = 126.8 + Math.random() * 0.2;
  return { lat, lng };
};

const Tripsub = () => {
  const [origin, setOrigin] = useState(generateRandomCoordinates());
  const [destination, setDestination] = useState(generateRandomCoordinates());
  const [waypoints, setWaypoints] = useState([
    generateRandomCoordinates(),
    generateRandomCoordinates(),
  ]);
  const [route, setRoute] = useState(null);
  const [error, setError] = useState(null);
  const [locations, setLocations] = useState({ origin: '', destination: '', waypoints: [] });

  useEffect(() => {
    const getRoute = async () => {
      const directions = await fetchDirections(origin, destination, waypoints);
      if (directions && directions.routes && directions.routes[0]) {
        setRoute(directions.routes[0]);
      } else {
        setError('경로를 가져오는데 실패했습니다.');
      }
    };
    getRoute();
  }, [origin, destination, waypoints]);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=${KAKAO_API_KEY}&libraries=services&autoload=false`;
    script.async = true;
    document.head.appendChild(script);

    script.onload = () => {
      window.kakao.maps.load(() => {
        const geocoder = new window.kakao.maps.services.Geocoder();

        const getAddress = (coords, callback) => {
          geocoder.coord2Address(coords.lng, coords.lat, (result, status) => {
            if (status === window.kakao.maps.services.Status.OK) {
              callback(result[0].address.address_name);
            } else {
              callback('주소를 찾을 수 없음');
            }
          });
        };

        // 출발지, 도착지, 경유지의 주소 가져오기
        getAddress(origin, (address) => setLocations((prev) => ({ ...prev, origin: address })));
        getAddress(destination, (address) => setLocations((prev) => ({ ...prev, destination: address })));
        waypoints.forEach((point, index) => {
          getAddress(point, (address) =>
            setLocations((prev) => {
              const updatedWaypoints = [...prev.waypoints];
              updatedWaypoints[index] = address;
              return { ...prev, waypoints: updatedWaypoints };
            })
          );
        });
      });
    };
  }, [origin, destination, waypoints]);

  if (error) {
    return <div className='error-message'>{error}</div>;
  }

  return (
    <div className="Tripsub-map">
      <Map
        center={{ lat: origin.lat, lng: origin.lng }}
        style={{ width: '75vw', height: '100vh' }}
        level={2}
      >
        <MapMarker position={{ lat: origin.lat, lng: origin.lng }} />
        <MapMarker position={{ lat: destination.lat, lng: destination.lng }} />
        {waypoints.map((point, index) => (
          <MapMarker key={index} position={{ lat: point.lat, lng: point.lng }} />
        ))}
        {route && route.sections && (
          <Polyline
            path={route.sections.flatMap(section =>
              section.roads ? section.roads.flatMap(road => extractPathFromVertexes(road.vertexes)) : []
            )}
            strokeWeight={2}
            strokeColor="black"
            strokeOpacity={0.7}
            strokeStyle="solid"
          />
        )}
      </Map>

      <div className="tripsub-location-info">
        <h3>여행경로 정보</h3>
        <div><strong>출발지:</strong> {locations.origin}</div>
        <div><strong>도착지:</strong> {locations.destination}</div>
        {locations.waypoints.map((address, index) => (
          <div key={index}><strong>경유지 {index + 1}:</strong> {address}</div>
        ))}
      </div>
    </div>
  );
};

export default Tripsub;
