import React, { useState, useEffect } from 'react';
import api from '../../api'; // 전역 설정된 axios 인스턴스 import
import UserNavbar from "../../components/UserNavbar/UserNavbar";
import '../Setting/Setting.css';

const Setting = () => {
  const [userInfo, setUserInfo] = useState({
    name: '',
    email: '',
    tel: '',
    birthday: '',
    username: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const username = sessionStorage.getItem('username');
        if (!username) {
          setError('로그인 정보가 없습니다. 다시 로그인 해주세요.');
          return;
        }

        const response = await api.get(`/my/${username}`);
        setUserInfo(response.data);
      } catch (error) {
        console.error('사용자 정보를 가져오는 데 실패했습니다:', error);
        setError('사용자 정보를 가져오는 데 실패했습니다.');
      }
    };

    fetchUserInfo();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserInfo(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleUpdate = async () => {
    try {
      const { email, tel, birthday } = userInfo; // 수정할 필드 선택
      await api.put('/update', {
        email,
        tel,
        birthday
      });
      alert('회원 정보가 성공적으로 업데이트되었습니다.');
      setIsEditing(false);
    } catch (error) {
      console.error('회원 정보 업데이트 실패:', error);
      alert('회원 정보를 업데이트하는 데 실패했습니다.');
    }
  };

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="Setting-page">
      <UserNavbar />
      <div className="Setting-page-container">
        <div className="Setting-page-container-section">
          <div className="Setting-page-container-User-img"></div>
          <div className="Setting-page-container-User-imgchange">프로필 변경하기</div>
          <div className="Setting-page-User">사용자 정보</div>

          <form>
            <div className="Setting-page-form-div">
              <div className='Setting-page-form-title'>
                <div className='Setting-page-form-text'>
                  <p className="Setting-page-file">이름</p>
                </div>
                <div className='Setting-page-form-button'>
                  <button type="button" className='Setting-page-form-update' onClick={() => setIsEditing(!isEditing)}>
                    {isEditing ? '수정 완료' : '수정하기'}
                  </button>
                </div>
              </div>
              <input
                type="text"
                name="name"
                value={userInfo.username || ''}
                onChange={handleChange}
                readOnly
              />
            </div>

            <div className="Setting-page-form-div">
              <p className="Setting-page-file">이메일</p>
              <input
                type="text"
                name="email"
                value={userInfo.email || ''}
                onChange={handleChange}
                readOnly={!isEditing}
              />
            </div>

            <div className="Setting-page-form-div">
              <p className="Setting-page-file">전화번호</p>
              <input
                type="text"
                name="tel"
                value={userInfo.tel || ''}
                onChange={handleChange}
                readOnly={!isEditing}
              />
            </div>

            <div className="Setting-page-form-div">
              <p className="Setting-page-file">생년월일</p>
              <input
                type="text"
                name="birthday"
                value={userInfo.birthday || ''}
                onChange={handleChange}
                readOnly={!isEditing}
              />
            </div>
          </form>

          {isEditing && (
            <button type="button" onClick={handleUpdate} className="Setting-page-save-button">
              저장
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Setting;
