import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UserNavbar from "../../components/UserNavbar/UserNavbar";
import '../Setting/Setting.css';

const Setting = () => {
    const [userInfo, setUserInfo] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchUserInfo = async () => {
            try {
                const username = JSON.parse(localStorage.getItem('user')).username;
                const response = await axios.get(`/my/${username}`);
                setUserInfo(response.data);
                setLoading(false);
            } catch (error) {
                console.error("사용자 정보를 가져오는 데 실패했습니다.", error);
                setLoading(false);
            }
        };

        fetchUserInfo();
    }, []);

    if (loading) {
        return <div>로딩 중...</div>;
    }

    if (!userInfo) {
        return <div>사용자 정보를 가져올 수 없습니다.</div>;
    }

    return (
        <div className="Setting-page">
            <UserNavbar />
            <div className="Setting-page-container">
                <div className="Setting-page-container-section">
                    <div className="Setting-page-container-User-img">
                        {/* 프로필 사진을 표시할 수 있는 img 태그 추가 */}
                        <img src="/path/to/profile/image.jpg" alt="User Profile" />
                    </div>
                    <div className="Setting-page-container-User-imgchange">프로필 변경하기</div>
                    <div className="Setting-page-User">사용자 정보</div>

                    <form>
                        <div className="Setting-page-form-div">
                            <p className="Setting-page-file">이름</p>
                            <input type="text" value={userInfo.name || '홍길동'} readOnly />
                        </div>

                        <div className="Setting-page-form-div">
                            <p className="Setting-page-file">이메일</p>
                            <input type="text" value={userInfo.email || 'email@example.com'} readOnly />
                        </div>

                        <div className="Setting-page-form-div">
                            <p className="Setting-page-file">아이디</p>
                            <input type="text" value={userInfo.username || 'user123'} readOnly />
                        </div>

                        <div className="Setting-page-form-div">
                            <p className="Setting-page-file">전화번호</p>
                            <input type="text" value={userInfo.tel || '010-1234-5678'} readOnly />
                        </div>

                        <div className="Setting-page-form-div">
                            <p className="Setting-page-file">가입일</p>
                            <input type="text" value={new Date(userInfo.signupDate).toLocaleDateString()} readOnly />
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Setting;
