import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Map, Polyline, CustomOverlayMap } from 'react-kakao-maps-sdk';
import '../Tripsub/Tripsub.css';
import { useLocation } from 'react-router-dom';

const KAKAO_API_KEY = '892b3fa8ad217de4b2ca0cae7c1ca67b';

// Directions API 호출 함수
const fetchDirections = async (origin, destination, waypoints) => {
    try {
        const response = await axios.post(
            'https://apis-navi.kakaomobility.com/v1/waypoints/directions',
            {
                origin: { x: origin.lng, y: origin.lat },
                destination: { x: destination.lng, y: destination.lat },
                waypoints: waypoints.map((point, index) => ({
                    name: `waypoint${index}`,
                    x: point.lng,
                    y: point.lat,
                })),
                priority: 'RECOMMEND',
                car_fuel: 'GASOLINE',
                car_hipass: false,
                alternatives: false,
                road_details: false,
            },
            {
                headers: {
                    Authorization: `KakaoAK ${KAKAO_API_KEY}`,
                    'Content-Type': 'application/json',
                },
            }
        );
        return response.data;
    } catch (error) {
        console.error('Error fetching directions:', error);
        return null;
    }
};

const Tripsub = () => {
    const location = useLocation();
    const routeData = [
      { lat: 37.1234, lng: 127.1234, name: "공수어촌체험마을", address: "강원도 속초시", image: "image_url1" },
      { lat: 37.2345, lng: 127.2325, name: "속초해변", address: "강원도 속초시", image: "image_url2" },
      // 추가 여행지 정보
  ]; // routeData에는 여행지 데이터가 포함되어 있어야 합니다

    const tripsubData = [
      { name: "공수어촌체험마을", address: "강원도 속초시", image: "image_url1" },
    { name: "속초해변", address: "강원도 속초시", image: "image_url2" },
    // 추가 여행지 정보
];
    
    const [route, setRoute] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (routeData.length >= 2) {
            const [origin, ...waypoints] = routeData;
            const destination = waypoints.pop(); // 마지막 경유지를 목적지로 사용

            fetchDirections(origin, destination, waypoints)
                .then((directions) => {
                    if (directions && directions.routes) {
                        setRoute(directions.routes[0]);
                    } else {
                        setError('경로를 가져오는 데 실패했습니다.');
                    }
                })
                .catch((err) => setError('API 요청 중 오류가 발생했습니다.'));
        } else {
            setError('출발지와 목적지를 설정하세요.');
        }
    }, [routeData]);

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    return (
        <div className="Tripsub-container" style={{ display: 'flex' }}>
            {/* 지도 부분 */}
            <div className="Tripsub-map" style={{ width: '70%', height: '100vh' }}>
                <Map
                    center={routeData[0] || { lat: 37.5665, lng: 126.9780 }} // 기본값을 서울로 설정
                    style={{ width: '100%', height: '100%' }}
                    level={3}
                >
                    {routeData.map((point, index) => (
                        <CustomOverlayMap
                            key={index}
                            position={{ lat: point.lat, lng: point.lng }}
                            yAnchor={1}
                        >
                            <div
                                style={{
                                    backgroundColor: 'blue',
                                    color: 'white',
                                    borderRadius: '50%',
                                    width: '30px',
                                    height: '30px',
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    fontWeight: 'bold',
                                }}
                            >
                                {index + 1}
                            </div>
                        </CustomOverlayMap>
                    ))}

                    {route && route.sections && (
                        <Polyline
                            path={route.sections.flatMap((section) =>
                                section.roads.flatMap((road) =>
                                    road.vertexes.reduce((acc, vertex, idx) => {
                                        if (idx % 2 === 0) {
                                            acc.push({ lng: vertex, lat: road.vertexes[idx + 1] });
                                        }
                                        return acc;
                                    }, [])
                                )
                            )}
                            strokeWeight={5}
                            strokeColor="black"
                            strokeOpacity={0.7}
                            strokeStyle="solid"
                        />
                    )}
                </Map>
            </div>

            {/* 여행경로 정보 부분 */}
            <div className="Tripsub-info" style={{ width: '30%', padding: '20px', backgroundColor: '#fff' }}>
                <h2>속초 여행경로</h2>
                {tripsubData.map((point, index) => (
                    <div key={index} className="Tripsub-location-info" style={{ marginBottom: '15px', border: '1px solid #ddd', padding: '10px', borderRadius: '8px' }}>
                        <p style={{ fontWeight: 'bold' }}>{`${index + 1}번째 여행지`}</p>
                        <img src={point.image} alt={point.name} style={{ width: '100%', height: 'auto', borderRadius: '8px' }} />
                        <div className="Tripsub-location-details" style={{ marginTop: '8px' }}>
                            <h3>{point.name}</h3>
                            <p>주소: {point.address}</p>
                        </div>
                    </div>
                ))}
                <button className="Tripsub-button" style={{ width: '100%', padding: '10px', backgroundColor: '#007bff', color: '#fff', border: 'none', borderRadius: '8px', cursor: 'pointer' }}>
                    나가기
                </button>
            </div>
        </div>
    );
};

export default Tripsub;
