import React, { useEffect, useState } from 'react';
import '../styles/User.css';
import UserNavbar from '../components/UserNavbar/UserNavbar';
import { useNavigate } from 'react-router-dom';


// useEffect, useState, useNavigate

const User = () => {
  const daysOfWeek = ['Ïùº', 'Ïõî', 'Ìôî', 'Ïàò', 'Î™©', 'Í∏à', 'ÌÜ†'];
  const [currentDate, setCurrentDate] = useState(new Date(2024, 9)); // Ï¥àÍ∏∞ ÎÇ†ÏßúÎ•º 6ÏõîÎ°ú ÏÑ§Ï†ï

  // ÌòÑÏû¨ ÏõîÏùò ÎÇ†Ïßú ÏàòÎ•º Í≥ÑÏÇ∞ÌïòÎäî Ìï®Ïàò
  const getDaysInMonth = (year, month) => {
    return new Date(year, month + 1, 0).getDate();
  };

  // Ïù¥Ï†Ñ Îã¨Î°ú Ïù¥ÎèôÌïòÎäî Ìï®Ïàò
  const goToPreviousMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() - 1));
  };

  // Îã§Ïùå Îã¨Î°ú Ïù¥ÎèôÌïòÎäî Ìï®Ïàò
  const goToNextMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() + 1));
  };

  // ÌòÑÏû¨ ÏõîÏùò ÎÇ†Ïßú ÏàòÏôÄ ÏãúÏûë ÏöîÏùºÏùÑ Í≥ÑÏÇ∞
  const daysInMonth = getDaysInMonth(currentDate.getFullYear(), currentDate.getMonth());
  const firstDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1).getDay();

  // ÎÇ†Ïßú Î∞∞Ïó¥ ÏÉùÏÑ± (ÎπàÏπ∏ Ìè¨Ìï®)
  const dates = Array.from({ length: firstDayOfMonth + daysInMonth }, (_, i) => {
    const date = i >= firstDayOfMonth ? (i - firstDayOfMonth + 1).toString().padStart(2, '0') : null;
    return date;
  });

  return (
    <div className="User-page">
      <UserNavbar/>
     
      <div className="calendar-container">

      <div className="calendar-container-header">
        <div className='User-calendar-container-header-title'>
          <button className='User-calender-button' onClick={goToPreviousMonth}>‚óÄ</button>
          <h2 className='calendar-title'>{currentDate.getFullYear()}ÎÖÑ {currentDate.toLocaleString('default', { month: 'long' })} </h2>
          <button className='User-calender-button' onClick={goToNextMonth}>‚ñ∂</button>
          </div>
        </div>  

        <div className="calendar-grid">
          {daysOfWeek.map(day => (
            <div className="calendar-day" key={day}>{day}</div>
          ))}
          {dates.map((date, index) => {
            // ÏùºÏöîÏùºÍ≥º ÌÜ†ÏöîÏùº ÌÅ¥ÎûòÏä§ Ï†ÅÏö©
            const dayOfWeek = (index % 7);
            const isSunday = dayOfWeek === 0;
            const isSaturday = dayOfWeek === 6;

            return (
              <div 
                className={`calendar-date ${date === 25 && currentDate.getMonth() === 5 ? 'highlight' : ''} 
                ${isSunday ? 'sunday' : ''} ${isSaturday ? 'saturday' : ''}`} 
                key={index}
              >
                {date ? date : ''}
              </div>
            );
          })}
        </div>
      </div>

 {/*
      <div className="User-Right-Sidebar-Container">
        <div className="Weather-widget">
          <p className='Weather-title-Container'>ÏÑúÏö∏ÌäπÎ≥ÑÏãú</p>
          
          <div className='User-Weather-section'>
          <div className="User-weather-icon">üå§</div>
          <p className='User-Weather-temperature'>23¬∞C</p>
          </div>

          <div className='User-Weather-section2'>
            
          </div>

        </div>
        
        <div className="schedule-widget">
          <h3>ÏùºÏ†ï</h3>
          <div className="schedule-item">ÎØ∏ÌåÖ 1</div>
          <div className="schedule-item">ÎØ∏ÌåÖ 2</div>
        </div>
      </div>  */}

    </div>
  );
};

export default User;
